mapper.select:
SELECT
dubbo_invoke.method,
dubbo_invoke.type,
SUM(dubbo_invoke.success) as success,
SUM(dubbo_invoke.elapsed) / SUM(dubbo_invoke.success) as elapsed,
dubbo_invoke.invoke_time DIV #{timeParticle} * #{timeParticle} as invokeTime
FROM dubbo_invoke
WHERE 1 = 1

    AND dubbo_invoke.invoke_date = DATE_FORMAT(#{invokeDate},'%Y-%m-%d')


    AND dubbo_invoke.invoke_date >= DATE_FORMAT(#{invokeDateFrom},'%Y-%m-%d')


    AND dubbo_invoke.invoke_date <= DATE_FORMAT(#{invokeDateTo},'%Y-%m-%d')


    AND dubbo_invoke.service = #{service}


    AND dubbo_invoke.method = #{method}


    AND dubbo_invoke.consumer = #{consumer}


    AND dubbo_invoke.provider = #{provider}


    AND dubbo_invoke.type = #{type}

GROUP BY dubbo_invoke.invoke_time DIV #{timeParticle} * #{timeParticle}

SELECT
dubbo_invoke.method,
dubbo_invoke.type,
SUM(dubbo_invoke.success) as success,
SUM(dubbo_invoke.elapsed) / SUM(dubbo_invoke.success) as elapsed,
((dubbo_invoke.invoke_time / #{timeParticle}) * #{timeParticle}) as invokeTime
FROM dubbo_invoke
WHERE 1 = 1

    AND dubbo_invoke.invoke_date = #{invokeDate}


    AND dubbo_invoke.invoke_date >= #{invokeDateFrom}


    AND dubbo_invoke.invoke_date <= #{invokeDateTo}


    AND dubbo_invoke.service = #{service}


    AND dubbo_invoke.method = #{method}


    AND dubbo_invoke.consumer = #{consumer}


    AND dubbo_invoke.provider = #{provider}


    AND dubbo_invoke.type = #{type}

GROUP BY dubbo_invoke.invoke_time / #{timeParticle,javaType=long} * #{timeParticle,javaType=long}

SELECT SUM(dubbo_invoke.success) as success, SUM(dubbo_invoke.failure) as failure, SUM(dubbo_invoke.elapsed) as elapsed, MAX(dubbo_invoke.max_elapsed) as maxElapsed, MAX(dubbo_invoke.max_concurrent) as maxConcurrent FROM dubbo_invoke WHERE 1 = 1
AND dubbo_invoke.invoke_date = DATE_FORMAT(#{invokeDate},'%Y-%m-%d')
AND dubbo_invoke.invoke_date >= DATE_FORMAT(#{invokeDateFrom},'%Y-%m-%d')
AND dubbo_invoke.invoke_date <= DATE_FORMAT(#{invokeDateTo},'%Y-%m-%d')
AND dubbo_invoke.service = #{service}
AND dubbo_invoke.method = #{method}
AND dubbo_invoke.consumer = #{consumer}
AND dubbo_invoke.provider = #{provider}
AND dubbo_invoke.type = #{type}

SELECT
    SUM(dubbo_invoke.success) as success,
    SUM(dubbo_invoke.failure) as failure,
    SUM(dubbo_invoke.elapsed) as elapsed,
    MAX(dubbo_invoke.max_elapsed) as maxElapsed,
    MAX(dubbo_invoke.max_concurrent) as maxConcurrent
FROM dubbo_invoke
WHERE
    1 = 1

    AND dubbo_invoke.invoke_date = #{invokeDate}


    AND dubbo_invoke.invoke_date >= #{invokeDateFrom}


    AND dubbo_invoke.invoke_date <= #{invokeDateTo}


    AND dubbo_invoke.service = #{service}


    AND dubbo_invoke.method = #{method}


    AND dubbo_invoke.consumer = #{consumer}


    AND dubbo_invoke.provider = #{provider}


    AND dubbo_invoke.type = #{type}

SELECT
 DISTINCT(method)
FROM dubbo_invoke
WHERE
1 = 1

    AND invoke_date = DATE_FORMAT(#{invokeDate},'%Y-%m-%d')


    AND dubbo_invoke.invoke_date >= DATE_FORMAT(#{invokeDateFrom},'%Y-%m-%d')


    AND dubbo_invoke.invoke_date <= DATE_FORMAT(#{invokeDateTo},'%Y-%m-%d')


    AND service = #{service}


    AND method = #{method}


    AND consumer = #{consumer}


    AND provider = #{provider}


    AND type = #{type}

SELECT
 DISTINCT(method)
FROM dubbo_invoke
WHERE
1 = 1

    AND invoke_date = #{invokeDate}


    AND dubbo_invoke.invoke_date >= #{invokeDateFrom}


    AND dubbo_invoke.invoke_date <= #{invokeDateTo}


    AND service = #{service}


    AND method = #{method}


    AND consumer = #{consumer}


    AND provider = #{provider}


