SELECT dubbo_invoke.service, dubbo_invoke.method, SUM(dubbo_invoke.success) as success FROM dubbo_invoke WHERE 1 = 1
AND dubbo_invoke.invoke_date = DATE_FORMAT(#{invokeDate},'%Y-%m-%d')
AND dubbo_invoke.invoke_date >= DATE_FORMAT(#{invokeDateFrom},'%Y-%m-%d')
AND dubbo_invoke.invoke_date <= DATE_FORMAT(#{invokeDateTo},'%Y-%m-%d')
AND dubbo_invoke.service = #{service}
AND dubbo_invoke.method = #{method}
AND dubbo_invoke.consumer = #{consumer}
AND dubbo_invoke.provider = #{provider}
AND dubbo_invoke.type = #{type}
GROUP BY dubbo_invoke.service, dubbo_invoke.method ORDER BY SUM(dubbo_invoke.success) DESC LIMIT 20

SELECT
dubbo_invoke.service,
dubbo_invoke.method,
SUM(dubbo_invoke.success) as success
FROM dubbo_invoke
WHERE
1 = 1

    AND dubbo_invoke.invoke_date = #{invokeDate, jdbcType=TIMESTAMP}


    AND dubbo_invoke.invoke_date >= #{invokeDateFrom, jdbcType=TIMESTAMP}


    AND dubbo_invoke.invoke_date <= #{invokeDateTo, jdbcType=TIMESTAMP}


    AND dubbo_invoke.service = #{service}


    AND dubbo_invoke.method = #{method}


    AND dubbo_invoke.consumer = #{consumer}


    AND dubbo_invoke.provider = #{provider}


    AND dubbo_invoke.type = #{type}

GROUP BY dubbo_invoke.service, dubbo_invoke.method
ORDER BY SUM(dubbo_invoke.success)  DESC LIMIT 20

